const hamburger=document.querySelector(".navbar__hamburger"),menu=document.querySelector(".navbar__list"),list=document.querySelectorAll(".navbar__list__item");hamburger.addEventListener("click",e=>{e.target&&menu.classList.toggle("menu--active"),list.forEach((e,t)=>{e.style.animation?e.style.animation="":e.style.animation=`fadeMenu 0.5s ease-in forwards ${t/7+.3}s`})}),document.addEventListener("scroll",()=>{576<=window.scrollY&&(menu.classList.remove("menu--active"),list.forEach(e=>{e.style.animation=""}))});